function titleCase(str) {
  let myArr = str.toLowerCase().split(" "); 
  for (var i = 0; i < myArr.length; i++) {
    var anotherArr = myArr[i].charAt(0).toUpperCase(); 
    var altered = myArr[i].replace(myArr[i][0], anotherArr);
    myArr[i] = (altered);
  }
  return myArr.join(" ")
}

